(13, 20, 'Bandit Camp', 'This one time... at Bandit Camp! Raid the Graywolf Renegades fort. Their ranks include some of the most wanted outlaws in the world!', 'This one time... at Bandit Camp! Raid the Graywolf Renegades fort. Their ranks include some of the most wanted outlaws in the world!', 'random/random-bandit.swf', 'none', 5, 650, 0, 20000, 0, 10469718, 'none', 0, 5, 'Bandits', 'mset-bandits.swf', '37,38,39,75', '0'),
(14, 23, 'Fiery Heart of Mt. Shining Star', 'You have braved the heat and come out the other side of the mountain only slightly toasted! is that burning hair that you smell?', 'You have braved the heat and come out the other side of the mountain only slightly toasted! is that burning hair that you smell?', 'quests/quest-firecave3.swf', 'none', 0, 150, 0, 20000, 0, 3513128, 'none', 10, 15, 'Elemental', 'mset-elementalfire.swf', '52,42', '0'),
